import io from "socket.io-client"
import { useEffect, useState } from "react";
import Chat from "./Chat";


const socket = io.connect("http://localhost:3001");

function App() {

  const [userName, setUserName] = useState();
  const [room, setRoom] = useState();


  const joinRoom = () => {
    if(userName !== "" && room !== ""){
        socket.emit("join__room", room)
    };

  }

  useEffect(()=>{


  }, [socket]);

  return (
    <div className="container" class= "flex justify-center items-center h-screen w-screen bg-gradient-to-r from-cyan-500 to-blue-500 ">

      <div className="joinRoomContainer" class="bg-slate-50 h-1/2  w-1/3 flex justify-center items-center rounded-md shadow-xl" >

        <div className="joinRoom" class=" bg-slate-50  w-80 h-80 text-center ">

          <h1 class="text-4xl font-bold py-4 text-center">Join Chat</h1>

          <input type="text" placeholder="Name" class="my-5"
            onChange={(event)=>{
              setUserName(event.target.value);
              }}
            />

          <br/>
          <br/>
          <input type="text" placeholder="Room..ID"  class="my-5"
            onChange={(event)=>{
              setRoom(event.target.value);
          }}
            />
          <br/>
          <br/>

          <button onClick={joinRoom} class="bg-blue-700 text-white rounded-full p-2 px-3 text-center mx-auto">Join Room</button>

        </div>

      </div>

      <br/>
      {/* <div className="chatContainer">
          <div className="chat">
            <Chat socket={socket} room={room} username={userName}/>
          </div>

        </div> */}

    </div>


  );
}

export default App;
