import io from "socket.io-client"
import { useEffect, useState } from "react";
import Chat from "./Chat";


const socket = io.connect("http://localhost:3001");

function App() {

  const [userName, setUserName] = useState();
  const [room, setRoom] = useState();


  const joinRoom = () => {
    if(userName !== "" && room !== ""){
        socket.emit("join__room", room)
    };

  }

  useEffect(()=>{


  }, [socket]);

  return (
    <div className="container" class= "flex justify-center items-center h-screen w-screen bg-slate-500 ">

      <div className="joinRoomContainer" class="bg-red-400 h-1/3  w-1/3 flex justify-center items-center" >

        <div className="joinRoom" class="">

          <h1 class="text-3xl font-bold underline ">Join Chat</h1>

          <input type="text" placeholder="Name" 
            onChange={(event)=>{
              setUserName(event.target.value);
              }}
            />

          <br/>

          <input type="text" placeholder="Room..ID" onChange={(event)=>{
            setRoom(event.target.value);
          }}
            />
          <br/>
          <br/>

          <button onClick={joinRoom} class="bg-blue-700 text-white rounded-full p-2 px-3">Join Room</button>

        </div>

      </div>
      <br/>
      <div className="chatContainer">
          <div className="chat">
            <Chat socket={socket} room={room} username={userName}/>
          </div>

        </div>

    </div>


  );
}

export default App;
